import * as React from 'react' ;

export class DisplayMapClass extends React.Component {

    mapRef = React.createRef();

    state = {
        // The map instance to use during Cleanup
        map: null
    };

    componentDidMount() {
        if ( ( this.mapRef !== null) || (this.mapRef !== undefined) )
        {
            const L = window.L;

            var osm = L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                maxZoom: 5, 
                attribution: '&copy; <a href="https://www.openstreetmap.org/copyright" target="_blank">OpenStreetMap</a> contributors</a> <a href="https://www.hotosm.org/" target="_blank">Tiles courtesy of Humanitarian OpenStreetMap Team</a>' });

            var temp = L.OWM.temperature( { showLegend: false, opacity: 0.5, appId: 'b4f215ca39fba72abf5e8b413859f1dd' } );
            var city = L.OWM.current( { interval: 15 , lang: 'en' } );//
        //console.log("L referst to >>>"+L.map ('map' , { center: new L.LatLng(51.5,10), zoom: 10, layers: [osm] } ).toString());
                                                       //^^^^^^^^^Australia center: new L.LatLng(144.96,-37.81)
            if (  (this.mapRef.current !== undefined) && (this.mapRef.current !== null) )
                { 
                    this.mapRef.current.off();
                    this.mapRef.current.remove(); 
                    this.mapRef.off();
                    this.mapRef.remove();
                    this.mapRef.dispose();
                    this.mapRef = null;
                    this.state.map.off();
                    this.state.map.remove();
                }
            const map = L.map (this.mapRef.current,

                                { center: new L.LatLng(51.5,10), 
                                  zoom: 5,
                                  layes: [osm]
                                } 
                             );
       // var baseMaps = { "OSM Standard":  osm };
       // var overlayMaps = { "Temperature": temp , "Cities": city };
       // var layerControl = L.control.layers(baseMaps,overlayMaps).addTo(this.mapRef.current);

       //const map = this.mapRef;

            this.setState({ map });
          }
     }

     componentWillUnmount(){

         //cleanup after the map to avoid memory leaks when this component exits the page
        if (  (this.mapRef.current !== undefined) && (this.mapRef.current !== null) )
            { 
                this.mapRef.current.off();
                this.mapRef.current.remove(); 
                this.mapRef.off();
                this.mapRef.remove();
                this.mapRef.dispose();
                this.mapRef = null;
                this.state.map.off();
                this.state.map.remove();
            }
         //----------
         if (!( this.state.map == null ) )
            {
                console.log('----call of disposing function for map---');
                 this.state.map = null;
            }
      }   


      render() {

         if (! (this.state.map == null) ) {
                console.log('download000' + (this.state.map));
                return( 

                    //Set a height on the map so it will display 
                    <div id='map' ref = {this.mapRef} style={ { border: "dotted" , height: "500px"} }>
                    </div>

                );
            }
            return(
               // return default value
               //<div> "loading ..... " </div>
               <div className="App">
                   <img src={logo} className="App-logo" alt="logo" />
               </div>
               );          
      }

} //~~~end of class
