        var cloudscls = L.OWM.cloudsClassic({appId: 'b4f215ca39fba72abf5e8b413859f1dd'});
        var rain = L.OWM.rain({appId: 'b4f215ca39fba72abf5e8b413859f1dd'});
        var wind = L.OWM.wind({appId: 'b4f215ca39fba72abf5e8b413859f1dd'});
        var city = L.OWM.current( { interval: 15 , lang: 'en' } );//
