                                  layers: osm
                                } 
                             );
       var baseMaps = { "OSM Standard":  osm };
       var overlayMaps = { "Temperature": temp , "Clouds" : clouds,"Wind": wind, "Cities": city };
       var layerControl = L.control.layers(baseMaps,overlayMaps).addTo(map);

